
public class ArithmaticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		float b = 2.2f;
		
		System.out.println("a+b = " + (a+b) ); //Performing Operations using different operators;
		System.out.println("a-b = " + (a-b) );
		System.out.println("a*b = " + (a*b) );
		System.out.println("a/b = " + (a/b) );
		System.out.println("a%b = " + (a%b) );
		

	}

}
