package com.new_package;

public class IncDecOperator2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int m = 5, n = 10;
System.out.println("m = " + m);
System.out.println("n = " + n);
System.out.println("++m=" + ++m);
System.out.println("n++=" + n++);
System.out.println("m = " +m);
System.out.println("n = " +n);
	}

}
