package com.loops;

public class ForEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i,j;

	System.out.println("The Numbers are");
	for(i=5;i>0;i--) {
	       for(j=5; j>=i; j--) 
		System.out.println(" "+j);	
	System.out.println();
	}
	}

}
