package newprogram;

public class LadderElse_If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      char city = 'P';
      if(city == 'B') 
    	  System.out.println("City is Bombay.");
    	  else if(city == 'N')
    		  System.out.println("City is Nagpur.");
    	  else if (city == 'P')
    		  System.out.println("City is Pune.");
    	  }
      
	}

